          onClick={onClose}
        />

        {selectedCard && (
          <>
            <img
              className="modal__image-card"
              src={selectedCard.link}
              alt={selectedCard.name}
              id="modal"
            />
            <h3 className="modal__title-text"> {selectedCard.name} </h3>
          </>
        )}
      </div>
    </section>
  );
}

export default ImagePopup;
